import ElectronStore from "electron-store";

interface StoreSchema {
  config: Record<string, any>;
}

type TypedElectronStore = ElectronStore<StoreSchema> & {
  get: <K extends keyof StoreSchema>(key: K) => StoreSchema[K];
  set: <K extends keyof StoreSchema>(key: K, value: StoreSchema[K]) => void;
  clear: () => void;
};

const store = new ElectronStore<StoreSchema>() as TypedElectronStore;
const localAsrManager = new LocalAsrManager();
const knowledgeRoot = path.join(__dirname, "knowledge");
const permanentKnowledgeDir = path.join(knowledgeRoot, "permanent");
const projectKnowledgeDir = path.join(knowledgeRoot, "projects");
const projectKnowledgeFile = path.join(projectKnowledgeDir, "current_project.md");

function toLayerName(filename: string): string {
  return filename
    .replace(/\.md$/i, "")
    .replace(/[_-]+/g, " ")
    .replace(/\b\w/g, (match) => match.toUpperCase());
}

function readKnowledgeFile(filePath: string): string | null {
  try {
    if (!fs.existsSync(filePath)) {
      return null;
    }
    return fs.readFileSync(filePath, "utf-8");
  } catch (error) {
    console.warn(`Failed to read knowledge file at ${filePath}:`, error);
    return null;
  }
}

function loadKnowledgeLayers(): KnowledgeContextPayload {
  const permanent: KnowledgeLayer[] = [];

  try {
    if (fs.existsSync(permanentKnowledgeDir)) {
      const files = fs
        .readdirSync(permanentKnowledgeDir)
        .filter((file) => file.toLowerCase().endsWith(".md"));

      files.forEach((file) => {
        const filePath = path.join(permanentKnowledgeDir, file);
        const content = readKnowledgeFile(filePath);
        if (typeof content === "string") {
          permanent.push({
            name: toLayerName(file),
            path: filePath,
            type: "permanent",
            content,
          });
        }
      });
    }
  } catch (error) {
    console.warn("Failed to enumerate permanent knowledge files", error);
  }

  let project: KnowledgeLayer | null = null;
  const projectContent = readKnowledgeFile(projectKnowledgeFile);
  if (typeof projectContent === "string") {
    project = {
      name: toLayerName(path.basename(projectKnowledgeFile)),
      path: projectKnowledgeFile,
      type: "project",
      content: projectContent,
    };
  }

  return { permanent, project };
}

function resolveLocalLlmConfig(config: any): LocalLlmConfig {
  const localConfig = config?.localLlm;
  if (!localConfig) {
    throw new Error("Local LLM configuration not found. Please configure a local model in settings.");
  }

  if (!localConfig.provider) {
    throw new Error("Local LLM provider is not specified.");
  }

  if (!localConfig.baseUrl) {
    throw new Error("Local LLM base URL is not specified.");
  }

  if (!localConfig.model) {
    throw new Error("Local LLM model is not specified.");
  }

  return {
    provider: localConfig.provider,
    baseUrl: localConfig.baseUrl,
    model: localConfig.model,
    temperature:
      typeof localConfig.temperature === "number" ? localConfig.temperature : undefined,
    topP: typeof localConfig.topP === "number" ? localConfig.topP : undefined,
    maxTokens:
      typeof localConfig.maxTokens === "number" ? localConfig.maxTokens : undefined,
    requestTimeoutMs:
      typeof localConfig.requestTimeoutMs === "number"
        ? localConfig.requestTimeoutMs
        : undefined,
  };
}

function normalizeMessages(rawMessages: any[]): ChatMessage[] {
  return (rawMessages || []).map((message) => {
    if (!message || typeof message !== "object") {
      throw new Error("Invalid message payload supplied to local LLM");
    }

    if (!message.role || !message.content) {
      throw new Error("Message is missing role or content");
    }

    if (Array.isArray(message.content)) {
      const flattened = message.content
        .map((part: any) => {
          if (typeof part === "string") {
            return part;
          }
          if (part && typeof part === "object" && "text" in part) {
            return String((part as any).text ?? "");
          }
          if (part && typeof part === "object" && "image_url" in part) {
            return `![Embedded image](${(part as any).image_url?.url ?? ""})`;
          }
          return "";
        })
        .join("\n");

      return {
        role: message.role,
        content: flattened,
      };
    }

    return {
      role: message.role,
      content: String(message.content ?? ""),
    };
  });
}

try {
  const initialConfig = store.get("config");
  if (initialConfig && typeof initialConfig === "object") {
    localAsrManager.configure(initialConfig.localAsr);
  }
} catch (error) {
  console.error("Failed to initialize local ASR config", error);
}

ipcMain.handle("get-config", () => {
  return store.get("config");
});

ipcMain.handle("set-config", (event, config) => {
  store.set("config", config);
  if (config && typeof config === "object") {
    localAsrManager.configure(config.localAsr);
  }
});

ipcMain.handle("load-knowledge-context", () => {
  return loadKnowledgeLayers();
});

ipcMain.handle("parsePDF", async (event, pdfBuffer) => {
  try {
    const pdf = require("pdf-parse");
    const data = await pdf(Buffer.from(pdfBuffer), {
      max: 0,
    });
    return { text: data.text };
  } catch (error) {
    return { error: "Failed to parse PDF: " + error.message };
  }
});

ipcMain.handle("process-image", async (event, imageData) => {
  try {
    const sharp = require("sharp");
    let image;
    if (imageData.startsWith("data:image")) {
      const base64Data = imageData.split(",")[1];
      const imageBuffer = Buffer.from(base64Data, "base64");
      image = sharp(imageBuffer);
    } else {
      throw new Error(
        "Invalid image input: expected Base64 encoded image data"
      );
    }
    const metadata = await image.metadata();
    return `Image size: ${metadata.width}x${metadata.height}, Format: ${metadata.format}`;
  } catch (error) {
    return { error: "Failed to process image: " + error.message };
  }
});

ipcMain.handle("highlightCode", async (event, code, language) => {
  return Prism.highlight(code, Prism.languages[language], language);
});

app.on("before-quit", () => {
  const config = store.get("config") || {};
  const apiInfo = {
    openai_key: config.openai_key || "",
    api_base: config.api_base || "",
    gpt_model: config.gpt_model || "",
    api_call_method: config.api_call_method || "direct",
    primaryLanguage: config.primaryLanguage || "en",
    deepgram_api_key: config.deepgram_api_key || "",
    localLlm: config.localLlm || null,
  };
  store.clear();
  store.set("config", apiInfo);
});

ipcMain.handle("get-system-audio-stream", async () => {
  try {
    const sources = await desktopCapturer.getSources({
      types: ["window", "screen"],
      fetchWindowIcons: false,
    });
    const audioSources = sources.filter(
      (source) =>
        source.name.toLowerCase().includes("sound") ||
        source.name.toLowerCase().includes("audio")
    );
    return audioSources.map((source) => source.id);
  } catch (error) {
    throw error;
  }
});

app.on("ready", () => {
  session.defaultSession.setDisplayMediaRequestHandler(
    (request, callback) => {
      desktopCapturer.getSources({ types: ["screen"] }).then((sources) => {
        callback({ video: sources[0], audio: "loopback" });
      });
    },
    { useSystemPicker: true }
  );

  ipcMain.handle(
    "transcribe-audio",
    async (event, audioBuffer: ArrayBuffer, config) => {
      try {
        const tempFilePath = path.join(
          app.getPath("temp"),
          `temp_audio_${Date.now()}.wav`
        );
        fs.writeFileSync(tempFilePath, Buffer.from(audioBuffer));

        const formData = new FormData();
        formData.append("file", fs.createReadStream(tempFilePath), {
          filename: "audio.wav",
          contentType: "audio/wav",
        });
        formData.append("model", "whisper-1");

        if (config.primaryLanguage && config.primaryLanguage !== "auto") {
          formData.append("language", config.primaryLanguage);
        }
        if (config.secondaryLanguage) {
          formData.append(
            "prompt",
            `This audio may contain ${config.primaryLanguage} and ${config.secondaryLanguage}.`
          );
        }

        const baseUrl = normalizeApiBaseUrl(config.api_base);
        const apiUrl = `${baseUrl}/audio/transcriptions`;
        const response = await axios.post(apiUrl, formData, {
          headers: {
            ...formData.getHeaders(),
            Authorization: `Bearer ${config.openai_key}`,
          },
          maxContentLength: Infinity,
          maxBodyLength: Infinity,
        });

        fs.unlinkSync(tempFilePath);

        return response.data;
      } catch (error) {
        throw error;
      }
    }
  );
});

ipcMain.handle("test-local-llm", async (_event, config) => {
  try {
    const resolvedConfig = resolveLocalLlmConfig(config);
    const client = new LocalLlmClient(resolvedConfig);
    await client.generate([
      { role: "system", content: "You are a test harness verifying connectivity." },
      { role: "user", content: "Respond with the word READY." },
    ]);
    return { success: true };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : "Unknown error occurred",
    };
  }
});

ipcMain.handle("call-local-llm", async (_event, { config, messages }) => {
  try {
    const resolvedConfig = resolveLocalLlmConfig(config);
    const client = new LocalLlmClient(resolvedConfig);
    const normalizedMessages = normalizeMessages(messages);
    const response = await client.generate(normalizedMessages);
    return { content: response };
  } catch (error) {
    return {
      error: error instanceof Error ? error.message : "Unknown error occurred",
    };
  }
});

ipcMain.handle("get-desktop-sources", async () => {
  try {
    const sources = await desktopCapturer.getSources({
      types: ["window", "screen"],
    });
    return sources.map((source) => ({
      id: source.id,
      name: source.name,
      thumbnail: source.thumbnail.toDataURL(),
    }));
  } catch (error) {
    return [];
  }
});

function normalizeApiBaseUrl(url: string): string {
  if (!url) return "https://api.openai.com/v1";
  url = url.trim();
  if (!url.startsWith("http://") && !url.startsWith("https://")) {
    url = "https://" + url;
  }
  if (!url.endsWith("/v1")) {
    url = url.endsWith("/") ? url + "v1" : url + "/v1";
  }
  return url;
}

let deepgramConnection: any = null;

localAsrManager.on("transcript", (payload) => {
  const target = localAsrManager.getCurrentWebContents();
  if (target) {
    target.send("local-asr-transcript", payload);
  }
});

localAsrManager.on("status", (payload) => {
  const target = localAsrManager.getCurrentWebContents();
  if (target) {
    target.send("local-asr-status", payload);
  }
});

ipcMain.handle("start-deepgram", async (event, config) => {
  try {
    if (!config.deepgram_key) {
      throw new Error("Deepgram API key lose");
    }
    const deepgram = createClient(config.deepgram_key);
    deepgramConnection = deepgram.listen.live({
      punctuate: true,
      interim_results: false,
      model: "general",
      language: config.primaryLanguage || "en",
      encoding: "linear16",
      sample_rate: 16000,
      endpointing: 1500,
    });

    deepgramConnection.addListener(LiveTranscriptionEvents.Open, () => {
      event.sender.send("deepgram-status", { status: "open" });
    });

    deepgramConnection.addListener(LiveTranscriptionEvents.Close, () => {
      event.sender.send("deepgram-status", { status: "closed" });
    });

    deepgramConnection.addListener(
      LiveTranscriptionEvents.Transcript,
      (data: any) => {
        if (
          data &&
          data.is_final &&
          data.channel &&
          data.channel.alternatives &&
          data.channel.alternatives[0]
        ) {
          const transcript = data.channel.alternatives[0].transcript;
          if (transcript) {
            event.sender.send("deepgram-transcript", {
              transcript,
              is_final: true,
            });
          }
        }
      }
    );

    deepgramConnection.addListener(
      LiveTranscriptionEvents.Error,
      (err: any) => {
        event.sender.send("deepgram-error", err);
      }
    );

    await new Promise((resolve, reject) => {
      deepgramConnection.addListener(LiveTranscriptionEvents.Open, resolve);
      deepgramConnection.addListener(LiveTranscriptionEvents.Error, reject);
      setTimeout(() => reject(new Error("Deepgram timeout")), 10000);
    });

    return { success: true };
  } catch (error) {
    return { success: false, error: error.message };
  }
});

ipcMain.on("send-audio-to-deepgram", (event, audioData) => {
  if (deepgramConnection) {
    try {
      const buffer = Buffer.from(audioData);
      deepgramConnection.send(buffer);
    } catch (error) {
      console.error("failed send data to Deepgram :", error);
    }
  }
});

ipcMain.handle("stop-deepgram", () => {
  if (deepgramConnection) {
    deepgramConnection.finish();
    deepgramConnection = null;
  }
});

ipcMain.handle("check-local-asr", async () => {
  return localAsrManager.getStatus();
});

ipcMain.handle("start-local-asr", async (event, options) => {
  return localAsrManager.startSession(event.sender, {
    language: options?.language,
    sampleRate: options?.sampleRate ?? 16000,
  });
});

ipcMain.on("send-audio-to-local-asr", (event, audioData) => {
  const success = localAsrManager.sendAudioChunk(audioData);
  if (!success) {
    event.sender.send("local-asr-status", {
      status: "error",
      message: "Failed to send audio to local ASR engine",
    });
  }
});

ipcMain.handle("stop-local-asr", async () => {
  await localAsrManager.stopSession();
  return { success: true };
});

// Handle reading prompt template files
ipcMain.handle("read-prompt-template", async (event, templateName: string) => {
  try {
    const promptsDir = path.join(__dirname, 'prompts');
    const templatePath = path.join(promptsDir, `${templateName}.md`);
    
    // Check if file exists
    if (!fs.existsSync(templatePath)) {
      return { error: `Template '${templateName}' not found` };
    }
    
    const content = fs.readFileSync(templatePath, 'utf-8');
    return { content };
  } catch (error) {
    return { error: `Failed to read template: ${error.message}` };
  }
});

// Handle listing available prompt templates
ipcMain.handle("list-prompt-templates", async () => {
  try {
    const promptsDir = path.join(__dirname, 'prompts');
    
    // Check if prompts directory exists
    if (!fs.existsSync(promptsDir)) {
      return { templates: [] };
    }
    
    const files = fs.readdirSync(promptsDir);
    const templates = files
      .filter(file => file.endsWith('.md'))
      .map(file => ({
        name: file.replace('.md', ''),
        filename: file
      }));
    
    return { templates };
  } catch (error) {
    return { error: `Failed to list templates: ${error.message}` };
  }
});
